import React, { useEffect, useState } from "react";
import { BASE_URL } from "../../utils/api";
import { useParams } from 'react-router-dom';
import api from "../../utils/api"; // ‚úÖ Axios instance

const EditProductForm = () => {
      const { id } = useParams(); // get product ID from URL
        // Category options
  const categories = [
    { value: "", label: "Select Category" },
    { value: "party wears", label: "PARTY WEARS" },
    { value: "semi party wear", label: "SEMI-PARTY WEARS" },
    { value: "co-ord sets", label: "CO-ORD SETS" },
    { value: "indo-western outfits", label: "INDO-WESTERN OUTFITS" },
    { value: "kurta", label: "KURTA" },
    { value: "saree", label: "SAREE" },

  ];

  const [formData, setFormData] = useState({
    sku: "",
    brand: "",
    weight: "",
    productName: "",
    category: "",
    deliveryTime: "",
    shortDescription: "",
    productDescription: "",
    careInstructions: "",
    stockQuantity: 0,
    price: 0,
    discount: 0,
    colorVariants: [""],
    material: [""],
    sizeVariants: {
      indian: [],
      pakistan: [],
    },
    images: [],
    // Fashion-specific fields
    neck: "",
    topDesignStyling: "",
    topFabric: "",
    bottomFabric: "",
    dupattaFabric: "",
    weavePattern: "",
    stitch: "",
    printOrPattern: "",
  });

  const [previewImages, setPreviewImages] = useState([]);
  const [existingImages, setExistingImages] = useState([]); // Track existing images
  const [newImages, setNewImages] = useState([]); // Track new uploaded images
  const [message, setMessage] = useState("");

  //fetch data of product

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await api.get(`/products/${id}`);
        const product = response.data.data;



        // Ensure all fields are set, including nested and array fields
        setFormData({
          sku: product.sku || "",
          brand: product.brand || "",
          weight: product.weight || "",
          productName: product.productName || "",
          category: product.category || "",
          deliveryTime: product.deliveryTime || "",
          shortDescription: product.shortDescription || "",
          productDescription: product.productDescription || "",
          careInstructions: product.careInstructions || "",
          stockQuantity: product.stockQuantity || 0,
          price: product.price || 0,
          discount: product.discount || 0,
          colorVariants: Array.isArray(product.colorVariants) ? product.colorVariants : [""],
          material: Array.isArray(product.material) ? product.material : [""],
          sizeVariants: {
            indian: Array.isArray(product.sizeVariants?.indian) ? product.sizeVariants.indian : [],
            pakistan: Array.isArray(product.sizeVariants?.pakistan) ? product.sizeVariants.pakistan : [],
          },
          images: [], // Keep empty for new file uploads
          neck: product.neck || "",
          topDesignStyling: product.topDesignStyling || "",
          topFabric: product.topFabric || "",
          bottomFabric: product.bottomFabic || "",
          dupattaFabric: product.dupattaFabric || "",
          weavePattern: product.weavePattern || "",
          stitch: product.stitch || "",
          printOrPattern: product.printOrPattern || "",
        });

        // Set existing images separately
        const productImages = product.images || [];
        setExistingImages(productImages);

        // Create proper image URLs for preview
        const imageUrls = productImages.map(img => {
          // If img is already a full URL, use it as is
          if (img.startsWith('http://') || img.startsWith('https://')) {
            return img;
          }
          // Otherwise, construct the URL with BASE_URL
          return `${BASE_URL}${img}`;
        });

        setPreviewImages(imageUrls);
      } catch (error) {
        console.error("Error fetching product:", error);
        setMessage("‚ùå Failed to fetch product data. Please try again.");
      }
    };

    fetchProduct();
  }, [id]);

  const handleChange = (e) => {
    const { name, value } = e.target;

    if (["colorVariants", "material"].includes(name)) {
      setFormData((prev) => ({ ...prev, [name]: value.split(",") }));
    } else {
      setFormData((prev) => ({ ...prev, [name]: value }));
    }
  };

  const handleSizeChange = (region, size) => {
    setFormData((prev) => {
      const currentSizes = prev.sizeVariants[region];
      const updatedSizes = currentSizes.includes(size)
        ? currentSizes.filter((s) => s !== size)
        : [...currentSizes, size];

      return {
        ...prev,
        sizeVariants: {
          ...prev.sizeVariants,
          [region]: updatedSizes,
        },
      };
    });
  };

  const handleImageChange = (e) => {
    const files = Array.from(e.target.files);
    setNewImages(files);
    setFormData((prev) => ({ ...prev, images: files }));

    // Create preview URLs for new images
    const newPreviews = files.map((file) => URL.createObjectURL(file));

    // If new images are selected, show only new images
    // If you want to show both existing and new images, use: [...existingImageUrls, ...newPreviews]
    setPreviewImages(newPreviews);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    const token = localStorage.getItem("token");

    if (!token) {
      setMessage("‚ùå No token found. Please log in.");
      return;
    }

    try {
      const form = new FormData();

      for (const key in formData) {
        if (key === "images") {
          // Only append new images if they exist
          if (newImages.length > 0) {
            newImages.forEach((file) => form.append("images", file));
          }
        } else if (key === "sizeVariants") {
          form.append("sizeVariants[indian]", formData.sizeVariants.indian.join(","));
          form.append("sizeVariants[pakistan]", formData.sizeVariants.pakistan.join(","));
        } else if (Array.isArray(formData[key])) {
          form.append(key, formData[key].join(","));
        } else {
          form.append(key, formData[key]);
        }
      }

      const response = await fetch(`${BASE_URL}products/edit/${id}`, {
