// // src/utils/api.js
// import axios from "axios";

// export const BASE_URL = "https://backend.pinkstories.ae/api";


// const api = axios.create({
//   baseURL: BASE_URL, 
//   withCredentials: true, // Optional: for cookies or tokens
// });

// export default api;

// src/utils/api.js
import axios from 'axios';

const api = axios.create({
  baseURL: 'https://backend.pinkstories.ae/api', // ✅ change this if testing locally
  withCredentials: false, // ✅ Set to true only if cookies/session used
  headers: {
    'Content-Type': 'application/json',
  },
});

// Optional: Attach token automatically
api.interceptors.request.use(
  (config) => {
    const token = localStorage.getItem('token');
    if (token) {
      config.headers.Authorization = `Bearer ${token}`;
    }
    return config;
  },
  (error) => Promise.reject(error)
);

export default api;
